import axios from 'axios';
import React, { Component } from 'react';
import Spotify from "spotify-web-api-js";
import PlaylistButton from "../components/PlaylistButton"
const spotifyWebApi = new Spotify();

class Youtube extends Component {
    constructor() {
        super();
        this.state = {
            apiKey: "AIzaSyAUlBPBvCwXcYNNahVcmWPKphhIs4YjaWQ",
            searchTerm: "",
            songName: ""
        }
        this.search = this.search.bind(this)
    }
    componentDidMount() {
        // this.search("chicken")
        this.getPlaylists()
        
    }
    createPlaylistButtons(playlists) {
        playlists.map(playlist => {
            console.log(playlist.name)
        });
    }
    getPlaylists () {
        spotifyWebApi.getUserPlaylists()
        .then((response) => {
            console.log(response);
            this.setState({
                Playlists: {name: "x"},
            })
            this.createPlaylistButtons(response.items)
        })
        .catch((error) => {
            console.log(error)
        });
    }

    async search(query) {
        axios
        .get("https://www.googleapis.com/youtube/v3/search?key=" + this.state.apiKey + "&q=" + query + "&part=snippet&maxResults=5&type=video")
        .then((response) => {
            console.log(response)
        })
        .catch((error) => {
            console.log(error)
        });
    }


    render() {
        return(
            <div>
                <div className="container">
                    <iframe id="player"
                            title ="player"
                            width="640" height="360"
                            src="https://www.youtube.com/embed/M7lc1UVf-VE?enablejsapi=1">
                    </iframe>    
                </div>
                <div id="Playlists">
                    
                </div>
            </div>
        )
    }
}

export default Youtube
