import axios from 'axios';
import React, { Component } from 'react';
import PlaylistButton from "../components/PlaylistButton"
import {
    Button
  } from "react-bootstrap";
import "../css/pickPlaylist.css"

class Youtube extends Component {
    constructor(props) {
        super(props);
        this.state = {
            apiKey: "AIzaSyAUlBPBvCwXcYNNahVcmWPKphhIs4YjaWQ",
            search_terms: this.props.location.tracks.map((track) => {
                var search_term = track.artists[0] + track.track_name;
                return [search_term, this.search(search_term)]
            })
        }
        const opts = {
            height: '400',
            width: '640',
            playerVars: {
                enablejsapi: 1,
                playlist: [],
            }
        }

        this.search = this.search.bind(this)
    }
    componentDidMount() {
        const tag = document.createElement('script');
        tag.src = 'https://www.youtube.com/iframe_api';
        const firstScriptTag = document.getElementsByTagName('script')[0];
        firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);
        window.onYouTubeIframeAPIReady = (e) => {
            this.YT = window.YT;
            this.player = new window.YT.Player('player', {
                height: '390',
                width: '640',
                videoId: 'M7lc1UVf-VE',
                events: {
                    'onReady': this.onPlayerReady,
                    'onStateChange': this.onPlayerStateChange
                }
            });
        }

    }
    onPlayerStateChange(e) {
        if (e.data === window.YT.PlayerState.PLAYING) {
            console.log('playing')
        }
    }

    onPlayerReady(e) {
        e.target.playVideo();
    }
    
    createPlaylistButtons() {
        this.setState.playlists.map(playlist => {return playlist.name});
    }

    async search(query) {
        axios
        .get("https://www.googleapis.com/youtube/v3/search?key=" + this.state.apiKey + "&q=" + query + "&part=snippet&maxResults=2type=video")
        .then((response) => {
            return (response.data.items[0].id.videoId)
            // console.log(response.data.items[0].snippet.thumbnails.high.url)
        })
        .catch((error) => {
            console.log(error)
        });
    }  

    render() {
        console.log(this.search_terms)
        return(
            <div>
                <div id="player"></div>
            </div>
        )
    }
}


  

export default Youtube

