import axios from 'axios';
import React, { Component } from 'react';
import YouTube from 'react-youtube';
import {
    Button
  } from "react-bootstrap";
import "../css/pickPlaylist.css"

const YTPlayer = require('yt-player')

class Youtube extends Component {
    constructor() {
        super();
        this.state = {
            apiKey: "AIzaSyAUlBPBvCwXcYNNahVcmWPKphhIs4YjaWQ",
            searchTerm: "",
            songName: "",
            playlists: [],
        }
        const opts = {
            height: '400',
            width: '640',
            playerVars: {
                enablejsapi: 1,
                playlist: [],
            }
        }
        let YT
        this.search = this.search.bind(this)
        this.loadVideo = this.loadVideo.bind(this)
    }
    componentDidMount() {
        const tag = document.createElement('script');
        tag.src = 'https://www.youtube.com/iframe_api';
        const firstScriptTag = document.getElementsByTagName('script')[0];
        firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);
        console.log(tag.src)
        //this.onYouTubeIframeAPIReady() 
    }

    onPlayerReady(event) {
        event.target.playVideo();
    }

    onPlayerStateChange(event) {
        var done = false;
        if (event.data == YT.PlayerState.PLAYING && !done) {
            setTimeout(stopVideo, 6000);
            done = true;
        }
    }

    stopVideo() {
        player.stopVideo();
    }
    
    createPlaylistButtons() {
        this.setState.playlists.map(playlist => {return playlist.name});
    }

    async search(query) {
        axios
        .get("https://www.googleapis.com/youtube/v3/search?key=" + this.state.apiKey + "&q=" + query + "&part=snippet&maxResults=5&type=video")
        .then((response) => {
            console.log(response)
        })
        .catch((error) => {
            console.log(error)
        });
    }  

    render() {
        return(
            <div>
                <div>
                    <script src="https://www.youtube.com/player_api"></script>
                </div>
                <div id="player">
                </div>
            </div>
        )
    }
}

function onYouTubeIframeAPIReady() {
    player = new window.YT.Player('player', {
        height: '390',
        width: '640',
        videoId: 'M7lc1UVf-VE',
        events: {
            'onReady': this.onPlayerReady,
            'onStateChange': this.onPlayerStateChange
        }
    });
}
  

export default Youtube

