import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Container,
  Row,
  Col,
  Alert,
  Breadcrumb,
  Button,
  Card,
  Form,
  Navbar,
} from "react-bootstrap";
import spotifyIcon from "./images/spotify-icon.png";

class App extends Component {
  constructor() {
    super();
    const params = this.getHashParams();
    this.state = {
      loggedIn: params.access_token ? true : false,
      nowPlaying: {
        name: "Not Checked",
        image: "k",
      },
    };
  }

  getHashParams() {
    var hashParams = {};
    var e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  render() {
    return (
      <div className="background">
        <div className="App">
          <Navbar className="App-navbar">Kojo Obeng-Arhin</Navbar>
        </div>
        <div>
          <Card id="App-card">
            <Card.Img
              src={require("./images/spotify-youtube.jpg")}
              alt="Spotify-Youtube"
            />
            <a href="http://localhost:8888">
              <Button id="primary-button" class="primary-button">
                Login With Spotify
              </Button>
            </a>
          </Card>
        </div>
        <div>Now Playing: {this.state.nowPlaying.name} </div>
        <div>
          <img src={this.state.nowPlaying.image} img />
        </div>
      </div>
    );
  }
}

export default App;
