import React, { Component } from "react";
import PlaylistPicker from "./components/PlaylistPicker";
import "./css/App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import {
  Container,
  Row,
  Col,
  Alert,
  Breadcrumb,
  Button,
  Card,
  Form,
  Navbar,
} from "react-bootstrap";
import Spotify from "spotify-web-api-js";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

const spotifyWebApi = new Spotify();

const SpotifyRouter = new Router("Router", [
  {
    path: "/login",
    name: "login",
  },
]);

// <Link to="/about">About</Link>
// <Route path="/about" component={about}
// <Route path="/login" component={Login}

var currentPath = window.location.pathname;
console.log(currentPath);

class App extends Component {
  constructor() {
    super();
    const params = this.getHashParams();
    this.state = {
      loggedIn: params.access_token ? true : false,
      nowPlaying: {
        name: "Not Checked",
        image: "k",
      },
    };

    if (params.access_token) {
      spotifyWebApi.setAccessToken(params.access_token);
    }

    this.getNowPlaying = this.getNowPlaying.bind(this);
  }

  getHashParams() {
    var hashParams = {};
    var e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  getNowPlaying() {
    spotifyWebApi.getMyCurrentPlaybackState().then((response) => {
      this.setState({
        nowPlaying: {
          name: response.item.name,
          image: response.item.album.images[0].url,
        },
      });
    });
  }

  // getPlaylists() {
  //   spotifyWebApi.

  // }

  render() {
    return (
      <div className="background">
        <div className="App">
          <Navbar className="App-navbar">Kojo Obeng-Arhin</Navbar>
        </div>
        <div>
          <Card id="App-card">
            <Card.Img
              src={require("./images/spotify-youtube.jpg")}
              alt="Spotify-Youtube"
            />
            <a href="http://localhost:8888/login">
              <Button id="primary-button" class="primary-button">
                Login With Spotify
              </Button>
            </a>
          </Card>
        </div>
        <div> Now Playing: {this.state.nowPlaying.name} </div>
        <div>
          {" "}
          <img src={this.state.nowPlaying.image} />{" "}
        </div>
        <div>
          <button onClick={this.getNowPlaying}>Check Now Playing</button>
        </div>
      </div>
    );
  }
}

export default App;
