import axios from 'axios';
import React, { Component } from 'react';
import Spotify from "spotify-web-api-js";
import {
    Button
  } from "react-bootstrap";
import "../css/pickPlaylist.css"

const spotifyWebApi = new Spotify();

class Youtube extends Component {
    constructor() {
        super();
        this.state = {
            apiKey: "AIzaSyAUlBPBvCwXcYNNahVcmWPKphhIs4YjaWQ",
            searchTerm: "",
            songName: "",
            playlists: [],
        }
        this.search = this.search.bind(this)
        
    }
    componentDidMount() {
        this.search("chicken")
        
    }
    createPlaylistButtons() {
        this.setState.playlists.map(playlist => {return playlist.name});
    }

    async search(query) {
        axios
        .get("https://www.googleapis.com/youtube/v3/search?key=" + this.state.apiKey + "&q=" + query + "&part=snippet&maxResults=5&type=video")
        .then((response) => {
            console.log(response)
        })
        .catch((error) => {
            console.log(error)
        });
    }

    async createAPlaylist(playlistName) {
        axios
        .post("https://www.googleapis.com/youtube/v3/search?key=" + this.state.apiKey + "&q=" + query + "&part=snippet&maxResults=5&type=video")
        .then((response) => {
            console.log(response)
        })
        .catch((error) => {
            console.log(error)
        });
    }

    authenticate() {
        return gapi.auth2.getAuthInstance()
            .signIn({scope: "https://www.googleapis.com/auth/youtube.readonly"})
            .then(function() { console.log("Sign-in successful"); },
                  function(err) { console.error("Error signing in", err); });
    }

    render() {
        //console.log(this.state.playlists)
        return(
            <div className="body">
                <script src="https://apis.google.com/js/api.js"></script>
                <Button onClick={() => this.authenticate()}> </Button>
                <div className="container">
                    <iframe id="player"
                            title ="player"
                            width="640" height="360"
                            src="https://www.youtube.com/embed/M7lc1UVf-VE?enablejsapi=1">
                    </iframe>    
                </div>
            </div>
        )
    }
}

export default Youtube
