import axios from 'axios';
import React, { Component } from 'react';
import {
    Button
  } from "react-bootstrap";
import "../css/pickPlaylist.css"

const YTPlayer = require('yt-player')

class Youtube extends Component {
    constructor() {
        super();
        this.state = {
            apiKey: "AIzaSyAUlBPBvCwXcYNNahVcmWPKphhIs4YjaWQ",
            searchTerm: "",
            songName: "",
            playlists: [],
        }
        this.search = this.search.bind(this)
        
    }
    componentDidMount() {
        this.createAPlayer();
        
    }
    createPlaylistButtons() {
        this.setState.playlists.map(playlist => {return playlist.name});
    }

    async search(query) {
        axios
        .get("https://www.googleapis.com/youtube/v3/search?key=" + this.state.apiKey + "&q=" + query + "&part=snippet&maxResults=5&type=video")
        .then((response) => {
            console.log(response)
        })
        .catch((error) => {
            console.log(error)
        });
    }

    createAPlayer() {
        const player = new YTPlayer("#player")
        player.load("GKSRyLdjsPA")
        player.play()
        console.log("hey")
    }  

    render() {
        //console.log(this.state.playlists)
        return(
            <div className="body">
                "chicken"
                <div id="players">
                    "Poop"
                    <script src='https://www.youtube.com/iframe_api' async></script>
                    {/* <Button onClick={() => this.createAPlayer()}> </Button>
                    {/* <div className="container">
                        <iframe id="player"
                                title ="player"
                                width="640" height="360"
                                src="https://www.youtube.com/embed/M7lc1UVf-VE?enablejsapi=1">
                        </iframe>    
                    </div> */} */}
                </div>
                <div id="player">
                </div>
            </div>
        )
    }
}

export default Youtube
