import React, { Component } from "react";
import {BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Home from "./pages/home.js"
import PickPlaylist from "./pages/pickPlaylist.js"
import Youtube from "./pages/youtube.js"
import Spotify from "spotify-web-api-js";
const spotifyWebApi = new Spotify();

class App extends Component {
  constructor(props) {
    super(props);
    this.params = this.getHashParams();
    this.state = {
      loggedIn: this.params.access_token ? true : false,
    };
    if (this.params.access_token) {
      spotifyWebApi.setAccessToken(this.params.refresh_token)
      localStorage.setItem("access_token", this.params.refresh_token)
    }
  }
  getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
        hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }
  render() {
    return (
      <div>
        <Router>
          <Switch>
            <Route path="/youtube" component={Youtube}/>
            <Route path="/pickPlaylist" component={PickPlaylist}/>
            <Route path="/" component={Home} />
          </Switch>
        </Router> 
      </div>
    );
  }
}

export default App;
