import React, { Component } from "react";
import {Link} from "react-router-dom"
import "bootstrap/dist/css/bootstrap.min.css";
import Spotify from "spotify-web-api-js";
import {
    Button
  } from "react-bootstrap";
import "../css/pickPlaylist.css"

const spotifyWebApi = new Spotify();

class PlaylistButton extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tracks: [],
            numOfTracks: 0,
        }
        this.handleClick.bind(this);
    }
    handleClick(playlistID) {
        const loopThroughTracks = [];
        this.setState({numOfTracks: 0});
        spotifyWebApi.getPlaylistTracks(playlistID).then((response) => {
            this.setState({numOfTracks: response.total}, () => {
                for (let i=0; i < Math.ceil(this.state.numOfTracks / 100); i++) {
                    loopThroughTracks.push(spotifyWebApi.getPlaylistTracks(playlistID, {offset: i*100}))
                }
            })
        })
       

        Promise.all(loopThroughTracks).then((response) => {
            console.log(this.state.numOfTracks)
                // this.setState({tracks: [this.state.tracks, ...response.items.map((item) => {
                //     return  {
                //         album_name: item.track.album.name,
                //         album_images: item.track.album.images,
                //         artists: item.track.artists.map((artist) => {return artist.name}),
                //         duration: item.track.duration_ms,
                //         track_name: item.track.name,
                //         date: item.added_at,
                //     }
                // })]});
        })
    }
    render () {
        return(
            <div className="playlist-button-and-image-container">
            <Button className="playlist-button" onClick={() => {this.handleClick(this.props.id)}}>{this.props.name}</Button>
            <img className="playlist-image" src={this.props.image} alt={this.props.name}/>
            <Link to= {{
                pathname: "/youtube",
                playlist: "x",
                tracks: this.state.tracks,
            }}>
            </Link>
        </div>
        )
    }
}

// <Button className="playlist-number">{this.props.index} </Button>
export default PlaylistButton
